-- load the http modulelocal io = require("io")local http = require("socket.http")local ltn12 = require("ltn12")local json = require ("json")require ("Util")--外网正式环境local PUBLIC_NET = "http://www.guokaodashi.com/gkapp_server/"--内网system环境local PRIVATE_NET = "http://system.guokaodashi.com/gkapp_server/"--实际连接local TAIL_URL = "onlineExam/list"--"onlineExam/getUserExamReport" --up5/user/login"--可变请求头local cache_token   = ""local platform     = 2 -- 1.Androd 2.iOSlocal version      = "4.4.3"local user_agent   = "GuoKao/4.4.3 (iPhone; iOS 9.3.3; Scale/2.00)"local device_id    = "cd60379e6fb30011f8d2ce61de486eb00c20f398298a1df7a0ec1ac7ad5c2179" -- iphonelocal user_token_map = {  ["time"]  = 1470457552263,  ["sign"]  = "b19a034da0ea490f0331f8014d41ae76",  ["uid"]   = "20160806122552263~629962554",  ["token"] = "IFe51txrQpM6T64HWOHDOx1kZC0Jb73pYT3E2gS/Q7SHRHH1d2x/3GLUEIKADRiG",  ["rm"]    = ""} --iphonelocal user_token = json.encode(user_token_map)--整合参数 转jsonlocal parameters = {  ["page"] = 1,  ["size"] = 10}--"{\"size\":\"10\",\"page\":1}"print("URL:"..PRIVATE_NET..TAIL_URL)local parameters_json = json.encode(parameters)--print("parameters_json ："..parameters_json)local request_body_map = {["data"] = parameters_json}util = Util.new()local request_body = json.encode(request_body_map)--util:sz_T2S(request_body_map)print("Content-Length :"..string.len(request_body))-- 异步请求local response_body = {}local res, code, response_headers = http.request{  url = PRIVATE_NET..TAIL_URL,  method = "POST",  --默认不写就是GET  headers =  {  --以下请求头参数固定    ["Content-Type"] = "application/x-www-form-urlencoded",    ["Content-Length"] =  string.len(request_body),    ["Transfer-Encoding"] = Identity,    ["Connection"] = "keep-alive",    ["Accept-Language"] = "zh-Hans-CN;q=1",    ["User-Agent"] = user_agent,    ["device_id"] = device_id,    ["userToken"] = user_token,    ["platform"] = platform,    ["version"] = version,  --唯一要变的请求头参数    ["cacheToken"] = cache_token,  },  -- source = ltn12.source.string([[data=parameters_json]]),  --source = ltn12.source.string(request_body),  source = ltn12.source.string(request_body),  sink = ltn12.sink.table(response_body)}-- 打印相关参数print("res = "..res)print("code = "..code)print("request_body = "..request_body)util:print_table(response_headers , "response_headers")print("request_body = "..request_body)util:print_table(response_body , "response_body")